import { SDKExternalUser } from './user.js';

type AuthMaterialBasic = {
    jwt?: string;
    refreshToken: string;
};
type AuthMaterial = {
    jwt: string;
    refreshToken: RefreshToken;
};
type AuthMaterialWithUser = AuthMaterial & {
    user: SDKExternalUser;
};
type AuthMaterialResponse = {
    jwt: string;
    refresh: RefreshToken;
    user: SDKExternalUser;
};
type AuthSession = {
    jwt: string;
    refreshToken: RefreshToken;
    userId: string;
};
interface RefreshToken {
    secret: string;
    expiresAt: string;
}
type OAuthProvider = "google" | "twitter";

export type { AuthMaterial, AuthMaterialBasic, AuthMaterialResponse, AuthMaterialWithUser, AuthSession, OAuthProvider };
